import { getPostsApi } from '../api';
import { actionTypes } from '../types';

const { GET_POSTS_INIT, GET_POSTS_SUCCESS, GET_POSTS_FAILURE, CLEAR_STORE, GET_CURRENT_POST_INIT } = actionTypes;

export const getPosts = (params) => (dispatch) => {
    dispatch({
        type: GET_POSTS_INIT,
    });
    getPostsApi(params)
        .then((response) => {
            dispatch({
                type: GET_POSTS_SUCCESS,
                payload: { ...response },
            });
        })
        .catch((error) => {
            dispatch({
                type: GET_POSTS_FAILURE,
                payload: { ...error },
            });
        });
};

export const clearStore = () => ({
    type: CLEAR_STORE,
});
