import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import Link from 'next/link';
import styled from 'styled-components';

import { getPosts, clearStore } from '../store/actions/postsActions';
import { postListDataSelector } from '../store/selectors';

import Header from '../components/Common/Header';
import Button from '../components/Common/Button';
import PostsList from '../components/MainPage/PostsList';

const IndexPage = () => {
    const dispatch = useDispatch();
    const postListData = useSelector((state) => postListDataSelector(state));

    useEffect(() => {
        dispatch(getPosts());
        return () => dispatch(clearStore());
    }, []);

    const { data } = postListData;
    const sortedData = data.length > 0 ? data.sort((a, b) => a.id > b.id) : data;
    console.log('sortedData: ', sortedData);
    return (
        <>
            <Header />
            <StyleDiv>
                <Button primary>
                    <Link href="/posts/new">
                        <a>Write post</a>
                    </Link>
                </Button>
            </StyleDiv>

            <PostsList postsArray={data} />
        </>
    );
};

export default IndexPage;

const StyleDiv = styled.div`
    text-align: end;
`;
