import React from 'react';
import PropTypes from 'prop-types';
import styled from 'styled-components';

import Button from '../../Common/Button';

const CreatePost = (props) => {
    return (
        <StyleDiv>
            <StyleInput {...props} placeholder="Title" name="title" />
            <StyleArea {...props} placeholder="Type your story..." name="body" />
            <Button primary>Upload</Button>
        </StyleDiv>
    );
};

CreatePost.propTypes = {
    onChange: PropTypes.func.isRequired,
};

export default CreatePost;

const StyleDiv = styled.div`
    width: 80%;
    margin: 0 auto;
    padding: 36px 0;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-wrap: wrap;
`;

const StyleInput = styled.input`
    width: 100%;
    height: 40px;
    border-radius: 5px;
    padding-left: 10px;
    font-size: 20px;
`;

const StyleArea = styled.textarea`
    width: 100%;
    min-height: 200px;
    border-radius: 5px;
    padding-left: 10px;
    padding-top: 10px;
    font-size: 16px;
    margin-top: 30px;
`;
