import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import Link from 'next/link';

import { getPosts, clearStore } from '../store/actions/postsActions';
import { postListDataSelector } from '../store/selectors';

import Header from '../components/Common/Header';
import Button from '../components/Common/Button';

const IndexPage = () => {
    const dispatch = useDispatch();
    useEffect(() => {
        dispatch(getPosts());
        return () => dispatch(clearStore());
    }, []);

    const postListData = useSelector((state) => useSelector(postListDataSelector(state)));

    return (
        <>
            <Header />
            <Button primary>
                <Link href="/posts/new">
                    <a>Write post</a>
                </Link>
            </Button>
        </>
    );
};

export default IndexPage;
